import { SET_ALERT, REMOVE_ALERT } from "../actions/types";


const initialState = [{
    id: 1,
    msg: 'Please log in',
    alertType: 'success'
}]

export const alert = (state = initialState, action) => {
    switch (action.type) {
        case SET_ALERT:
            return [...state, action.payload]
        case REMOVE_ALERT:   
     return state

    }
}
