import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { getCurrentProfile } from '../../actions/profile';
import Spinner from '../layout/Spinner';
import { Fragment } from 'react';

const Dashboard = ({
  getCurrentProfile,
  auth: { user },
  profile: { profile, loading },
}) => {
  useEffect(() => {
    getCurrentProfile();
  }, []);

  return loading && profile === null ? (
    <Spinner />
  ) : (
    <Fragment>
      <h1 class="large text-primary">Dashboard</h1>
      <p class="lead">
        <i class="fas fa-user"></i> Welcome, {user && user.name}
      </p>
      {profile !== null ? (
        <Fragment>
          <div class="dash-buttons">
            <a href="edit-profile.html" class="btn btn-light">
              <i class="fas fa-user-circle text-primary"></i> Edit Profile
            </a>
            <a href="add-experience.html" class="btn btn-light">
              <i class="fab fa-black-tie text-primary"></i> Add Experience
            </a>
            <a href="add-education.html" class="btn btn-light">
              <i class="fas fa-graduation-cap text-primary"></i> Add Education
            </a>
          </div>
        </Fragment>
      ) : (
        <Fragment><p>You have not yet setup a profile, please add some info</p></Fragment>
      )}
    </Fragment>
  );
};

Dashboard.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  auth: state.auth,
  profile: state.profile,
});

export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);
