import axios from 'axios'
import {setAlert} from './alert'
import setAuthToken from '../utils/setAuthToken'
import {
    REGISTER_FAIL,
    REGISTER_SUCCESS,
    USER_LOADED,
    AUTH_ERROR
} from './types'

